import React, { useRef, useState } from 'react';
import * as XLSX from 'xlsx';

const hardware_projects = [
  "射箭比赛", "布袋球", "百米短跑", "划艇比赛", "滑板比赛", "掰手腕",
  "投篮大师", "谁是大力士", "新乒乓球大战", "跳绳抛射", "耐力跳跃", "乒乓球大战",
  "打水漂", "飞盘射击", "toio 保龄球", "太鼓小火车", "魔法烟花秀", "魔杖泡泡龙",
  "合成大西瓜", "投篮挑战"
];

// 固定门店顺序
const storeOrder = [
  "汇景校区",
  "小北校区",
  "东峻校区",
  "滨江校区",
  "国贸校区",
  "吾悦校区",
  "番禺校区",
  "广钢校区",
  "前海校区",
  "白云校区",
  "东山口校区",
  "活动组"
];

function normalizeStoreName(name) {
  if (!name) return '';
  return name.replace(/\s/g, '').replace(/　/g, ''); // 去除所有空格（半角和全角）
}

function App() {
  const inputRef = useRef();
  const [tableData, setTableData] = useState([]);
  const [summary, setSummary] = useState({});

  const handleFile = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const data = await file.arrayBuffer();
    const workbook = XLSX.read(data);
    const sheet = workbook.Sheets[workbook.SheetNames[0]];
    const json = XLSX.utils.sheet_to_json(sheet);

    // 分类、类型转换、门店名标准化，并保留原始门店名
    json.forEach(row => {
      row.originName = row['门店'];
      row['门店'] = normalizeStoreName(row['门店']);
      row['项目类型'] = hardware_projects.includes(row['小类']) ? '硬件项目' : '纯软项目';
      row['时长（小时）'] = Number(row['时长（小时）']) || 0;
    });

    // 获取所有标准化门店
    const stores = Array.from(new Set(json.map(row => row['门店'])));

    // 每个门店，分别统计纯软/硬件项目前3
    const table = stores.map(store => {
      const storeRows = json.filter(row => row['门店'] === store);
      // 取第一个原始门店名用于显示
      const originStoreName = storeRows[0]?.originName || store;
      // 纯软
      const soft = storeRows.filter(r => r['项目类型'] === '纯软项目');
      const softTop3 = Object.entries(
        soft.reduce((acc, cur) => {
          acc[cur['小类']] = (acc[cur['小类']] || 0) + cur['时长（小时）'];
          return acc;
        }, {})
      )
        .sort((a, b) => b[1] - a[1])
        .slice(0, 3)
        .map(([项目, 时长]) => ({ 项目, 时长: 时长.toFixed(2) }));

      // 硬件
      const hard = storeRows.filter(r => r['项目类型'] === '硬件项目');
      const hardTop3 = Object.entries(
        hard.reduce((acc, cur) => {
          acc[cur['小类']] = (acc[cur['小类']] || 0) + cur['时长（小时）'];
          return acc;
        }, {})
      )
        .sort((a, b) => b[1] - a[1])
        .slice(0, 3)
        .map(([项目, 时长]) => ({ 项目, 时长: 时长.toFixed(2) }));

      return {
        门店: store, // 标准化门店名
        originName: originStoreName, // 原始门店名
        纯软项目: softTop3,
        硬件项目: hardTop3
      };
    });

    setTableData(table);

    // 汇总
    const summaryObj = {};
    ['纯软项目', '硬件项目'].forEach(type => {
      const typeRows = json.filter(r => r['项目类型'] === type);
      const top3 = Object.entries(
        typeRows.reduce((acc, cur) => {
          acc[cur['小类']] = (acc[cur['小类']] || 0) + cur['时长（小时）'];
          return acc;
        }, {})
      )
        .sort((a, b) => b[1] - a[1])
        .slice(0, 3)
        .map(([项目, 时长]) => ({ 项目, 时长: 时长.toFixed(2) }));
      summaryObj[type] = top3;
    });
    setSummary(summaryObj);
  };

  // 导出功能
  const handleExport = () => {
    // 组装导出数据
    const exportData = storeOrder
      .map(orderStore => {
        const normalizedOrderStore = normalizeStoreName(orderStore);
        return tableData.find(row => row.门店 === normalizedOrderStore);
      })
      .filter(Boolean)
      .map(row => {
        const obj = { 门店: row.originName || row.门店 };
        row['纯软项目'].forEach((item, idx) => {
          obj[`纯软No.${idx + 1}`] = item.项目;
          obj[`纯软时长${idx + 1}`] = item.时长;
        });
        row['硬件项目'].forEach((item, idx) => {
          obj[`硬件No.${idx + 1}`] = item.项目;
          obj[`硬件时长${idx + 1}`] = item.时长;
        });
        return obj;
      });
    // 汇总
    const summaryRow = { 门店: '汇总' };
    (summary['纯软项目'] || []).forEach((item, idx) => {
      summaryRow[`纯软No.${idx + 1}`] = item.项目;
      summaryRow[`纯软时长${idx + 1}`] = item.时长;
    });
    (summary['硬件项目'] || []).forEach((item, idx) => {
      summaryRow[`硬件No.${idx + 1}`] = item.项目;
      summaryRow[`硬件时长${idx + 1}`] = item.时长;
    });
    exportData.push(summaryRow);

    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, XLSX.utils.json_to_sheet(exportData), '整理结果');
    XLSX.writeFile(wb, '整理结果.xlsx');
  };

  return (
    <div className="App" style={{ padding: 40 }}>
      <h2>Excel项目分类整理工具</h2>
      <input type="file" accept=".xlsx,.xls" ref={inputRef} onChange={handleFile} />
      <p>上传Excel文件，自动整理并在页面显示结果，如需导出请点击下方按钮。</p>
      <ul>
        <li>表头需包含：门店、小类、时长（小时）</li>
        <li>支持.xlsx/.xls格式</li>
      </ul>

      {tableData.length > 0 && (
        <div style={{ overflowX: 'auto', marginTop: 32 }}>
          <table border="1" cellPadding="6" style={{ borderCollapse: 'collapse', minWidth: 1100, textAlign: 'center' }}>
            <thead>
              <tr>
                <th rowSpan="2" style={{ background: '#f6f6f6' }}>门店/分组</th>
                <th colSpan="6" style={{ background: '#e3f0fc' }}>纯软项目</th>
                <th colSpan="6" style={{ background: '#f5e3fc' }}>硬件项目</th>
              </tr>
              <tr>
                <th style={{ background: '#e3f0fc' }}>No.1 项目</th>
                <th style={{ background: '#e3f0fc' }}>体验时长(h)</th>
                <th style={{ background: '#e3f0fc' }}>No.2 项目</th>
                <th style={{ background: '#e3f0fc' }}>体验时长(h)</th>
                <th style={{ background: '#e3f0fc' }}>No.3 项目</th>
                <th style={{ background: '#e3f0fc' }}>体验时长(h)</th>
                <th style={{ background: '#f5e3fc' }}>No.1 项目</th>
                <th style={{ background: '#f5e3fc' }}>体验时长(h)</th>
                <th style={{ background: '#f5e3fc' }}>No.2 项目</th>
                <th style={{ background: '#f5e3fc' }}>体验时长(h)</th>
                <th style={{ background: '#f5e3fc' }}>No.3 项目</th>
                <th style={{ background: '#f5e3fc' }}>体验时长(h)</th>
              </tr>
            </thead>
            <tbody>
              {storeOrder
                .map(orderStore => {
                  const normalizedOrderStore = normalizeStoreName(orderStore);
                  return tableData.find(row => row.门店 === normalizedOrderStore);
                })
                .filter(Boolean)
                .map((row, idx) => (
                  <tr key={idx}>
                    <td style={{ background: '#f6f6f6' }}>{row.originName || row.门店}</td>
                    {[0, 1, 2].map(i => (
                      <React.Fragment key={'soft' + i}>
                        <td>{row.纯软项目[i]?.项目 || '-'}</td>
                        <td>{row.纯软项目[i]?.时长 || '-'}</td>
                      </React.Fragment>
                    ))}
                    {[0, 1, 2].map(i => (
                      <React.Fragment key={'hard' + i}>
                        <td>{row.硬件项目[i]?.项目 || '-'}</td>
                        <td>{row.硬件项目[i]?.时长 || '-'}</td>
                      </React.Fragment>
                    ))}
                  </tr>
                ))}
              {/* 汇总行 */}
              {summary['纯软项目'] && summary['硬件项目'] && (
                <tr style={{ fontWeight: 'bold', background: '#f9f9f9' }}>
                  <td>汇总</td>
                  {[0, 1, 2].map(i => (
                    <React.Fragment key={'soft_sum' + i}>
                      <td>{summary['纯软项目'][i]?.项目 || '-'}</td>
                      <td>{summary['纯软项目'][i]?.时长 || '-'}</td>
                    </React.Fragment>
                  ))}
                  {[0, 1, 2].map(i => (
                    <React.Fragment key={'hard_sum' + i}>
                      <td>{summary['硬件项目'][i]?.项目 || '-'}</td>
                      <td>{summary['硬件项目'][i]?.时长 || '-'}</td>
                    </React.Fragment>
                  ))}
                </tr>
              )}
            </tbody>
          </table>
        </div>
      )}

      {tableData.length > 0 && (
        <button style={{ marginTop: 32 }} onClick={handleExport}>
          导出Excel结果
        </button>
      )}

      {tableData.length === 0 && (
        <div style={{color: 'red', marginTop: 20}}>未解析到有效数据，请检查Excel表头和内容！</div>
      )}
    </div>
  );
}

export default App;
