import React, { useRef, useState } from 'react';
import * as XLSX from 'xlsx';

const hardware_projects = [
  "射箭比赛", "布袋球", "百米短跑", "划艇比赛", "滑板比赛", "掰手腕",
  "投篮大师", "谁是大力士", "新乒乓球大战", "跳绳抛射", "耐力跳跃", "乒乓球大战",
  "打水漂", "飞盘射击", "toio 保龄球", "太鼓小火车", "魔法烟花秀", "魔杖泡泡龙",
  "合成大西瓜", "投篮挑战"
];

function App() {
  const inputRef = useRef();
  const [storeTop3, setStoreTop3] = useState([]);
  const [summary, setSummary] = useState([]);

  const handleFile = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const data = await file.arrayBuffer();
    const workbook = XLSX.read(data);
    const sheet = workbook.Sheets[workbook.SheetNames[0]];
    const json = XLSX.utils.sheet_to_json(sheet);

    // 分类
    json.forEach(row => {
      row['项目类型'] = hardware_projects.includes(row['小类']) ? '硬件项目' : '纯软项目';
    });

    // 门店前3
    const storeMap = {};
    json.forEach(row => {
      const key = `${row['门店']}_${row['项目类型']}`;
      if (!storeMap[key]) storeMap[key] = {};
      storeMap[key][row['小类']] = (storeMap[key][row['小类']] || 0) + row['时长（小时）'];
    });
    const storeTop3Arr = [];
    Object.entries(storeMap).forEach(([key, value]) => {
      const [store, type] = key.split('_');
      Object.entries(value)
        .sort((a, b) => b[1] - a[1])
        .slice(0, 3)
        .forEach(([proj, hours]) => {
          storeTop3Arr.push({ 门店: store, 项目类型: type, 小类: proj, 总时长: hours });
        });
    });

    // 汇总
    const sumMap = {};
    json.forEach(row => {
      const key = `${row['项目类型']}_${row['小类']}`;
      sumMap[key] = (sumMap[key] || 0) + row['时长（小时）'];
    });
    const summaryArr = [];
    ['硬件项目', '纯软项目'].forEach(type => {
      Object.entries(sumMap)
        .filter(([k]) => k.startsWith(type))
        .sort((a, b) => b[1] - a[1])
        .slice(0, 3)
        .forEach(([k, hours]) => {
          summaryArr.push({ 项目类型: type, 小类: k.split('_')[1], 总时长: hours });
        });
    });

    setStoreTop3(storeTop3Arr);
    setSummary(summaryArr);
  };

  const handleExport = () => {
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, XLSX.utils.json_to_sheet(storeTop3), '门店前3项目');
    XLSX.utils.book_append_sheet(wb, XLSX.utils.json_to_sheet(summary), '汇总');
    XLSX.writeFile(wb, '整理结果.xlsx');
  };

  return (
    <div className="App" style={{ padding: 40 }}>
      <h2>Excel项目分类整理工具</h2>
      <input type="file" accept=".xlsx,.xls" ref={inputRef} onChange={handleFile} />
      <p>上传Excel文件，自动整理并在页面显示结果，如需导出请点击下方按钮。</p>
      <ul>
        <li>表头需包含：门店、小类、时长（小时）</li>
        <li>支持.xlsx/.xls格式</li>
      </ul>

      {storeTop3.length > 0 && (
        <div>
          <h3>门店前3项目</h3>
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>门店</th>
                <th>项目类型</th>
                <th>小类</th>
                <th>总时长</th>
              </tr>
            </thead>
            <tbody>
              {storeTop3.map((row, idx) => (
                <tr key={idx}>
                  <td>{row.门店}</td>
                  <td>{row.项目类型}</td>
                  <td>{row.小类}</td>
                  <td>{row.总时长}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {summary.length > 0 && (
        <div style={{ marginTop: 32 }}>
          <h3>汇总（最受欢迎的3个项目）</h3>
          <table border="1" cellPadding="6">
            <thead>
              <tr>
                <th>项目类型</th>
                <th>小类</th>
                <th>总时长</th>
              </tr>
            </thead>
            <tbody>
              {summary.map((row, idx) => (
                <tr key={idx}>
                  <td>{row.项目类型}</td>
                  <td>{row.小类}</td>
                  <td>{row.总时长}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {(storeTop3.length > 0 || summary.length > 0) && (
        <button style={{ marginTop: 32 }} onClick={handleExport}>
          导出Excel结果
        </button>
      )}
    </div>
  );
}

export default App;
