import React, { useRef, useState } from 'react';
import * as XLSX from 'xlsx';

const hardware_projects = [
  "Â∞ÑÁÆ≠ÊØîËµõ", "Â∏ÉË¢ãÁêÉ", "ÁôæÁ±≥Áü≠Ë∑ë", "ÂàíËâáÊØîËµõ", "ÊªëÊùøÊØîËµõ", "Êé∞ÊâãËÖï",
  "ÊäïÁØÆÂ§ßÂ∏à", "Ë∞ÅÊòØÂ§ßÂäõÂ£´", "Êñ∞‰πí‰πìÁêÉÂ§ßÊàò", "Ë∑≥Áª≥ÊäõÂ∞Ñ", "ËÄêÂäõË∑≥Ë∑É", "‰πí‰πìÁêÉÂ§ßÊàò",
  "ÊâìÊ∞¥ÊºÇ", "È£ûÁõòÂ∞ÑÂáª", "toio ‰øùÈæÑÁêÉ", "Â§™ÈºìÂ∞èÁÅ´ËΩ¶", "È≠îÊ≥ïÁÉüËä±ÁßÄ", "È≠îÊùñÊ≥°Ê≥°Èæô",
  "ÂêàÊàêÂ§ßË•øÁìú", "ÊäïÁØÆÊåëÊàò"
];

// Âõ∫ÂÆöÈó®Â∫óÈ°∫Â∫è
const storeOrder = [
  "Ê±áÊôØÊ†°Âå∫",
  "Â∞èÂåóÊ†°Âå∫",
  "‰∏úÂ≥ªÊ†°Âå∫",
  "Êª®Ê±üÊ†°Âå∫",
  "ÂõΩË¥∏Ê†°Âå∫",
  "ÂêæÊÇ¶Ê†°Âå∫",
  "Áï™Á¶∫Ê†°Âå∫",
  "ÂπøÈí¢Ê†°Âå∫",
  "ÂâçÊµ∑Ê†°Âå∫",
  "ÁôΩ‰∫ëÊ†°Âå∫",
  "‰∏úÂ±±Âè£Ê†°Âå∫",
  "Ê¥ªÂä®ÁªÑ"
];

// Èó®Â∫óÂà´ÂêçÊò†Â∞ÑË°®
const storeAliasMap = {
  'Ëã±ËçîÊ±áÊôØ': 'Ê±áÊôØÊ†°Âå∫',
  'Ëã±ËçîÂ∞èÂåó': 'Â∞èÂåóÊ†°Âå∫',
  'Ëã±Ëçî‰∏úÂ≥ª': '‰∏úÂ≥ªÊ†°Âå∫',
  'Ëã±ËçîÊª®Ê±ü': 'Êª®Ê±üÊ†°Âå∫',
  'Ëã±ËçîÊµ∑Âè£ÂõΩË¥∏Ê†°Âå∫': 'ÂõΩË¥∏Ê†°Âå∫',
  'Ëã±ËçîÊµ∑Âè£ÂêæÊÇ¶Ê†°Âå∫': 'ÂêæÊÇ¶Ê†°Âå∫',
  'Ëã±ËçîÁï™Á¶∫ÂπøÂú∫Ê†°Âå∫': 'Áï™Á¶∫Ê†°Âå∫',
  'Ëã±ËçîÂàõÈÄ†‰πêÂõ≠ÂπøÈí¢Ê†°Âå∫': 'ÂπøÈí¢Ê†°Âå∫',
  'Ëã±ËçîÊ∑±Âú≥ÂâçÊµ∑ÁßëÂÖ¥Ê†°Âå∫': 'ÂâçÊµ∑Ê†°Âå∫',
  'Ëã±Ëçî‰∏úÂ±±Âè£Ê†°Âå∫': '‰∏úÂ±±Âè£Ê†°Âå∫',
  'Ëã±ËçîÂ≤≠ÂçóÊñ∞‰∏ñÁïåÊ†°Âå∫': 'ÁôΩ‰∫ëÊ†°Âå∫',
  // ...Â¶ÇÊúâÂÖ∂ÂÆÉË°•ÂÖÖ
};

function normalizeStoreName(name) {
  if (!name) return '';
  return name.replace(/\s/g, '').replace(/„ÄÄ/g, ''); // ÂéªÈô§ÊâÄÊúâÁ©∫Ê†ºÔºàÂçäËßíÂíåÂÖ®ËßíÔºâ
}

function App() {
  const inputRef = useRef();
  const [tableData, setTableData] = useState([]);
  const [summary, setSummary] = useState({});

  const handleFile = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const data = await file.arrayBuffer();
    const workbook = XLSX.read(data);
    const sheet = workbook.Sheets[workbook.SheetNames[0]];
    const json = XLSX.utils.sheet_to_json(sheet);

    // ÂàÜÁ±ª„ÄÅÁ±ªÂûãËΩ¨Êç¢„ÄÅÈó®Â∫óÂêçÊ†áÂáÜÂåñÔºåÂπ∂‰øùÁïôÂéüÂßãÈó®Â∫óÂêç
    json.forEach(row => {
      row.originName = row['Èó®Â∫ó'];
      const norm = normalizeStoreName(row['Èó®Â∫ó']);
      row['Ê†áÂáÜÈó®Â∫ó'] = storeAliasMap[row['Èó®Â∫ó']] || storeAliasMap[norm] || row['Èó®Â∫ó'];
      row['È°πÁõÆÁ±ªÂûã'] = hardware_projects.includes(row['Â∞èÁ±ª']) ? 'Á°¨‰ª∂È°πÁõÆ' : 'Á∫ØËΩØÈ°πÁõÆ';
      row['Êó∂ÈïøÔºàÂ∞èÊó∂Ôºâ'] = Number(row['Êó∂ÈïøÔºàÂ∞èÊó∂Ôºâ']) || 0;
    });

    // ‰ª•ÂÆûÈôÖÂá∫Áé∞ÁöÑÈó®Â∫óÂàÜÁªÑÁªüËÆ°
    const storeMap = {};
    json.forEach(row => {
      if (!storeMap[row['Ê†áÂáÜÈó®Â∫ó']]) {
        storeMap[row['Ê†áÂáÜÈó®Â∫ó']] = {
          Ê†áÂáÜÈó®Â∫ó: row['Ê†áÂáÜÈó®Â∫ó'],
          originName: row.originName,
          Á∫ØËΩØÈ°πÁõÆ: [],
          Á°¨‰ª∂È°πÁõÆ: []
        };
      }
      storeMap[row['Ê†áÂáÜÈó®Â∫ó']][row['È°πÁõÆÁ±ªÂûã']].push(row);
    });

    const table = Object.values(storeMap).map(store => {
      // Á∫ØËΩØÈ°πÁõÆÂâç3
      const softTop3 = Object.entries(
        store['Á∫ØËΩØÈ°πÁõÆ'].reduce((acc, cur) => {
          acc[cur['Â∞èÁ±ª']] = (acc[cur['Â∞èÁ±ª']] || 0) + cur['Êó∂ÈïøÔºàÂ∞èÊó∂Ôºâ'];
          return acc;
        }, {})
      )
        .sort((a, b) => b[1] - a[1])
        .slice(0, 3)
        .map(([È°πÁõÆ, Êó∂Èïø]) => ({ È°πÁõÆ, Êó∂Èïø: Êó∂Èïø.toFixed(2) }));

      // Á°¨‰ª∂È°πÁõÆÂâç3
      const hardTop3 = Object.entries(
        store['Á°¨‰ª∂È°πÁõÆ'].reduce((acc, cur) => {
          acc[cur['Â∞èÁ±ª']] = (acc[cur['Â∞èÁ±ª']] || 0) + cur['Êó∂ÈïøÔºàÂ∞èÊó∂Ôºâ'];
          return acc;
        }, {})
      )
        .sort((a, b) => b[1] - a[1])
        .slice(0, 3)
        .map(([È°πÁõÆ, Êó∂Èïø]) => ({ È°πÁõÆ, Êó∂Èïø: Êó∂Èïø.toFixed(2) }));

      return {
        Ê†áÂáÜÈó®Â∫ó: store.Ê†áÂáÜÈó®Â∫ó,
        originName: store.originName,
        Á∫ØËΩØÈ°πÁõÆ: softTop3,
        Á°¨‰ª∂È°πÁõÆ: hardTop3
      };
    });

    setTableData(table);

    // Ê±áÊÄª
    const summaryObj = {};
    ['Á∫ØËΩØÈ°πÁõÆ', 'Á°¨‰ª∂È°πÁõÆ'].forEach(type => {
      const typeRows = json.filter(r => r['È°πÁõÆÁ±ªÂûã'] === type);
      const top3 = Object.entries(
        typeRows.reduce((acc, cur) => {
          acc[cur['Â∞èÁ±ª']] = (acc[cur['Â∞èÁ±ª']] || 0) + cur['Êó∂ÈïøÔºàÂ∞èÊó∂Ôºâ'];
          return acc;
        }, {})
      )
        .sort((a, b) => b[1] - a[1])
        .slice(0, 3)
        .map(([È°πÁõÆ, Êó∂Èïø]) => ({ È°πÁõÆ, Êó∂Èïø: Êó∂Èïø.toFixed(2) }));
      summaryObj[type] = top3;
    });
    setSummary(summaryObj);
  };

  // ÂØºÂá∫ÂäüËÉΩ
  const handleExport = () => {
    // ÂØºÂá∫È°∫Â∫è‰∏éÈ°µÈù¢‰∏ÄËá¥
    const exportData = storeOrder.map(orderStore => {
      const row = tableData.find(r => normalizeStoreName(r.Ê†áÂáÜÈó®Â∫ó) === normalizeStoreName(orderStore));
      const obj = { Èó®Â∫ó: orderStore };
      [0, 1, 2].forEach(idx => {
        obj[`Á∫ØËΩØNo.${idx + 1}`] = row?.Á∫ØËΩØÈ°πÁõÆ[idx]?.È°πÁõÆ || '-';
        obj[`Á∫ØËΩØÊó∂Èïø${idx + 1}`] = row?.Á∫ØËΩØÈ°πÁõÆ[idx]?.Êó∂Èïø || '-';
      });
      [0, 1, 2].forEach(idx => {
        obj[`Á°¨‰ª∂No.${idx + 1}`] = row?.Á°¨‰ª∂È°πÁõÆ[idx]?.È°πÁõÆ || '-';
        obj[`Á°¨‰ª∂Êó∂Èïø${idx + 1}`] = row?.Á°¨‰ª∂È°πÁõÆ[idx]?.Êó∂Èïø || '-';
      });
      return obj;
    });

    // Ê±áÊÄª
    const summaryRow = { Èó®Â∫ó: 'Ê±áÊÄª' };
    [0, 1, 2].forEach(idx => {
      summaryRow[`Á∫ØËΩØNo.${idx + 1}`] = summary['Á∫ØËΩØÈ°πÁõÆ']?.[idx]?.È°πÁõÆ || '-';
      summaryRow[`Á∫ØËΩØÊó∂Èïø${idx + 1}`] = summary['Á∫ØËΩØÈ°πÁõÆ']?.[idx]?.Êó∂Èïø || '-';
    });
    [0, 1, 2].forEach(idx => {
      summaryRow[`Á°¨‰ª∂No.${idx + 1}`] = summary['Á°¨‰ª∂È°πÁõÆ']?.[idx]?.È°πÁõÆ || '-';
      summaryRow[`Á°¨‰ª∂Êó∂Èïø${idx + 1}`] = summary['Á°¨‰ª∂È°πÁõÆ']?.[idx]?.Êó∂Èïø || '-';
    });
    exportData.push(summaryRow);

    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, XLSX.utils.json_to_sheet(exportData), 'Êï¥ÁêÜÁªìÊûú');
    XLSX.writeFile(wb, 'Êï¥ÁêÜÁªìÊûú.xlsx');
  };

  return (
    <div className="App" style={{ padding: 40 }}>
      <h2>ÊúÄÂèóÊ¨¢Ëøé‰∫íÂä®È°πÁõÆÊï∞ÊçÆ</h2>
      <input type="file" accept=".xlsx,.xls" ref={inputRef} onChange={handleFile} />
      <p>‰∏ä‰º† Excel Êñá‰ª∂ÔºåËá™Âä®Êï¥ÁêÜÂπ∂Âú®È°µÈù¢ÊòæÁ§∫ÁªìÊûúÔºåÂ¶ÇÈúÄÂØºÂá∫ËØ∑ÁÇπÂáª‰∏ãÊñπÊåâÈíÆ„ÄÇ</p>
      <ul>
        <li>Ë°®Â§¥ÈúÄÂåÖÂê´ÔºöÈó®Â∫ó„ÄÅÂ∞èÁ±ª„ÄÅÊó∂ÈïøÔºàÂ∞èÊó∂Ôºâ</li>
        <li>ÊîØÊåÅ .xlsx/.xls Ê†ºÂºè</li>
      </ul>

      {tableData.length > 0 && (
        <div style={{ overflowX: 'auto', marginTop: 32 }}>
          <table border="1" cellPadding="6" style={{ borderCollapse: 'collapse', minWidth: 1100, textAlign: 'center' }}>
            <thead>
              <tr>
                <th rowSpan="2" style={{ background: '#f6f6f6' }}>2025/7/1ÔΩû7/31</th>
                <th colSpan="6" style={{ background: '#e3f0fc' }}>Á∫ØËΩØÈ°πÁõÆ</th>
                <th colSpan="6" style={{ background: '#f5e3fc' }}>Á°¨‰ª∂È°πÁõÆ</th>
              </tr>
              <tr>
                <th style={{ background: '#e3f0fc' }}>üèÜ No.1 È°πÁõÆ</th>
                <th style={{ background: '#e3f0fc' }}>‰ΩìÈ™åÊó∂Èïø (h)</th>
                <th style={{ background: '#e3f0fc' }}>ü•à No.2 È°πÁõÆ</th>
                <th style={{ background: '#e3f0fc' }}>‰ΩìÈ™åÊó∂Èïø (h)</th>
                <th style={{ background: '#e3f0fc' }}>ü•â No.3 È°πÁõÆ</th>
